mapper.select:
SELECT info.*, pt.Detained_person_type_desc, at.appeal_type_desc, dps.Detained_person_status_desc, ac.appeal_channel_desc, ks.keep_status_desc, user1.username as submitUser, user2.username as reviewUser FROM t01_detained_person_info info LEFT JOIN t99_detained_person_type pt ON info.Detained_person_type_cd = pt.Detained_person_type_cd LEFT JOIN t99_appeal_type at ON info.appeal_channel_cd = at.appeal_type_cd LEFT JOIN t99_detained_person_status dps ON dps.Detained_person_status_cd = info.Detained_person_status_cd LEFT JOIN t99_appeal_channel ac ON ac.appeal_channel_cd = info.appeal_channel_cd LEFT JOIN t99_keep_status ks ON ks.keep_status_cd = info.keep_status_cd left join sys_user user1 on user1.user_id = info.submit_user_id left join sys_user user2 on user2.user_id = info.review_user_id WHERE 1=1
and info.submit_date=#{param.submitDate}
AND info.keep_status_cd = #{param.keepStatusCd}
